d[1] = global.get('D1');
d[2] = global.get('D2');
d[3] = global.get('D3');
d[4] = global.get('D4');

// Zurückseten der Auswertung, damit diese neu beginnen kann.
global.set('D0',0);
global.set('D1',0);
