global.set('D3',0);
global.set('D4',0);


// Ausertung der analgoen Eingänge. Sind alle innerhalb von 20 ADC values, 0.3V?
// dann  LED = nicht Rot. Vollausschlag = 50V, 4096 Werte = 12.2mV / ADC
// 20 ADC values = 0.25V = 0.5%
// 40 ADC values = 0.50V = 1.0%
// 80 ADC values = 1.00V = 2.0% = -+1%
if (global.get('AnalogMaxDiff')<80)
    LED.R = 0;
else
    LED.R = 1;

// Ausertung digitaler Eingänge / Ausgänge dann LED = nichr Blau.
if (d[0] > 1 && d[1] > 1 &&d[2] > 1 &&d[3] > 1 &&d[4] > 1)
    LED.B = 0;
else 
    LED.B = 1;

// Grünes LED leuchtet nur, wenn die digitalen Eingägne gezählt werden und
// wenn die analgoen Eingänge innerhalb der definierten Differenz zueinander sind..
LED.G = 1 - LED.R - LED.B;

msg.payload = d;
msg.maxDiff = global.get('AnalogMaxDiff');

return [msg,{payload:LED}];

